mapper.select:
select sum(amount) from orders


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


        and status = #{status}

select count(id) from orders


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


        and status = #{status}

select count(id) from user


        and create_time >= #{beginTime}


        and create_time <= #{endTime}
